CREATE TABLE wishlist_item (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL UNIQUE, price BIGINT NOT NULL, is_public TINYINT(1) DEFAULT '1' NOT NULL, user_id BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE wishlist_user (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL, is_male TINYINT(1) DEFAULT '1', age BIGINT DEFAULT 18 NOT NULL, email VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE wishlist_item ADD CONSTRAINT wishlist_item_user_id_wishlist_user_id FOREIGN KEY (user_id) REFERENCES wishlist_user(id) ON DELETE CASCADE;
